<style>
:root{--ink:#1e2933;--muted:#5b6b76;--bg:#fff;--soft:#f7f9fb;--line:#e6e9ec;--brand:#0a7a3b;--badge:#25d366}
body{margin:0;background:var(--bg);color:var(--ink);font:16px/1.6 system-ui,-apple-system,Segoe UI,Roboto,Arial,sans-serif}
.gsg-wrap{max-width:1120px;margin:0 auto;padding:16px}
.gsg-hero{display:grid;gap:16px;padding:8px 0 12px;border-bottom:1px solid var(--line)}
.gsg-hero .kicker{font-weight:700;color:var(--muted)}
.gsg-hero h1{margin:.2em 0 .3em;font-size:clamp(1.6rem,2.4vw+1rem,2.2rem);line-height:1.2}
.gsg-hero p{margin:0;color:var(--muted)}
.gsg-hero .cta{display:flex;flex-wrap:wrap;gap:10px;margin-top:10px}
.btn{display:inline-block;padding:.65rem 1rem;border-radius:.6rem;border:1px solid transparent;text-decoration:none;font-weight:700;white-space:nowrap}
.btn.primary{background:var(--brand);color:#fff}
.btn.soft{background:var(--soft);border-color:var(--line);color:var(--ink)}
.btn.wa,.btn.tg{background:#20b657;color:#fff;border-color:#20b657}
.btn.wa:hover,.btn.tg:hover{background:var(--badge)}
nav.toc{display:flex;flex-wrap:wrap;gap:8px;margin:14px 0}
nav.toc a{display:inline-block;padding:.35rem .6rem;border:1px solid var(--line);border-radius:999px;background:#fff;color:var(--ink);text-decoration:none;font-weight:600}
section{padding:8px 0 12px}
h2{font-size:1.35rem;margin:1.2em 0 .6em}
h3{font-size:1.05rem;margin:1em 0 .5em}
.note{background:var(--soft);border:1px solid var(--line);border-radius:.6rem;padding:.8rem 1rem;margin:.8rem 0}
.table{overflow-x:auto;border:1px solid var(--line);border-radius:.6rem;background:#fff;margin:.8rem 0}
.table table{width:100%;border-collapse:collapse;min-width:560px}
